import React from 'react';
// motion
import {motion} from "framer-motion";
// variants
import {fadeIn} from "../variants";

// Handler to open email client
const handleSubmit = (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    const name = formData.get('name');
    const email = formData.get('email');
    const message = formData.get('message');

    const mailtoLink = `mailto:tristanarts@icloud.com?subject=Contact from ${name}&body=Message: ${message}%0D%0AFrom: ${name} (${email})`;
    window.location.href = mailtoLink;
};

const Testimonials = () => {
    return (
        <section id='testimonials'
                 className='py-16 lg:section h-screen'>
            <div className='container mx-auto'>
                <div className='flex flex-col lg:flex-row'>
                    {/* text */}
                    <motion.div
                        variants={fadeIn('right', 0.3)}
                        initial="hidden"
                        whileInView={"show"}
                        viewport={{once: false, amount: 0.3}}
                        className='flex-1 flex justify-start tems-center'>
                        <div className='mb-6'>
                            <h4 className='text-xl uppercase text-accent font-medium mb-2 tracking wide'>
                                Testimonials
                            </h4>
                            <h2 className='text-[45px] lg:text-[90px] leading-none mb-12'>
                                What people think of <br/> Arts ICT!
                            </h2>
                        </div>
                    </motion.div>
                    {/* form */}
                    <motion.div
                        variants={fadeIn('left', 0.3)}
                        initial="hidden"
                        whileInView={"show"}
                        viewport={{once: false, amount: 0.3}}
                        className='flex-1 border rounded-2xl flex flex-col gap-y-6 pb-24 p-6 items-start'>
                        <div class="container mx-auto px-6 py-20">
                            <h2 class="text-4xl font-bold text-center mb-8 text-gradient">
                                Testimonials
                            </h2>
                            <div class="flex flex-wrap">
                                <div class="w-full md:w-1/3 px-2 mb-4">
                                    <div class="bg-slate-200 rounded shadow py-2">
                                        <p class="text-accent text-base px-6 mb-5">
                                            "Working with Arts ICT transformed our digital presence. Tristan's
                                            innovative approach and meticulous attention to detail have
                                            not only enhanced the functionality of our website but also significantly
                                            improved user engagement. Tristan's passion for technology and commitment to
                                            excellence are evident in every project they undertake. Partnering with Arts
                                            ICT has been one of the best decisions we've made, and we look forward to
                                            continuing this fruitful collaboration."
                                        </p>
                                        <p class="text-accent text-xs md:text-sm px-6">
                                            SherLobke
                                        </p>
                                    </div>
                                </div>
                                <div class="w-full md:w-1/3 px-2 mb-4">
                                    <div class="bg-white rounded shadow py-2">
                                        <p class="text-gray-800 text-base px-6 mb-5">
                                            Ik wist niet dat mijn kleine terriër, Daisy, zulke verbazingwekkende
                                            speurvaardigheden had
                                            tot we de cursussen bij SherLobke volgden. Dank je, Lobke, voor het openen
                                            van deze nieuwe
                                            wereld voor ons!
                                        </p>
                                        <p class="text-gray-500 text-xs md:text-sm px-6">
                                            Emma & Daisy
                                        </p>
                                    </div>
                                </div>
                                <div class="w-full md:w-1/3 px-2 mb-4">
                                    <div class="bg-white rounded shadow py-2">
                                        <p class="text-gray-800 text-base px-6 mb-5">
                                            SherLobke heeft ons geholpen een sterke band op te bouwen en elkaar op een
                                            heel nieuw niveau
                                            te begrijpen. De technieken en het plezier dat we hebben beleefd tijdens de
                                            speurlessen zijn
                                            onbetaalbaar.
                                        </p>
                                        <p class="text-gray-500 text-xs md:text-sm px-6">
                                            Lucas & Bella
                                        </p>

                                    </div>
                                </div>
                            </div>
                        </div>
                    </motion.div>

                </div>
            </div>
        </section>
    );


};

export default Testimonials;
