import React, {useRef} from 'react';
// motion
import { motion } from "framer-motion";
// variants
import { fadeIn } from "../variants";
// Import emailjs
import emailjs from '@emailjs/browser';

const btn = document.getElementById('button');

document.getElementById('form')
    .addEventListener('submit', function(event) {
        event.preventDefault();

        btn.value = 'Sending...';

        const serviceID = 'default_service';
        const templateID = 'template_epp41tf';

        emailjs.sendForm(serviceID, templateID, this)
            .then(() => {
                btn.value = 'Send Email';
                alert('Sent!');
            }, (err) => {
                btn.value = 'Send Email';
                alert(JSON.stringify(err));
            });
    });
const Contact = () => {
    const form = useRef();
    const btn = useRef();

    const sendEmail = (e) => {
        e.preventDefault();
        if(btn.current) btn.current.value = 'Sending...';

        const serviceID = 'default_service';
        const templateID = 'template_epp41tf';
        // Note: Replace 'YOUR_USER_ID' with your actual EmailJS user ID
        const userID = 'YOUR_USER_ID';

        emailjs.sendForm(serviceID, templateID, form.current, userID)
            .then(() => {
                if(btn.current) btn.current.value = 'Send Email';
                alert('Sent!');
            }, (err) => {
                if(btn.current) btn.current.value = 'Send Email';
                alert(JSON.stringify(err));
            });
    };

    return (
        <section id='contact' className='py-16 lg:section h-screen'>
            <div className='container mx-auto'>
                <div className='flex flex-col lg:flex-row'>
                    {/* text */}
                    <motion.div
                        variants={fadeIn('right', 0.3)}
                        initial="hidden"
                        whileInView={"show"}
                        viewport={{once: false, amount: 0.3}}
                        className='flex-1 flex justify-start items-center'>
                        <div>
                            <h4 className='text-xl uppercase text-accent font-medium mb-2 tracking-wide'>
                                Get in touch
                            </h4>
                            <h2 className='text-[45px] lg:text-[90px] leading-none mb-12'>
                                Let's work together!
                            </h2>
                        </div>
                    </motion.div>
                    {/* form */}
                    <motion.form
                        ref={form} onSubmit={sendEmail} id="form"
                        variants={fadeIn('left', 0.3)}
                        initial="hidden"
                        whileInView={"show"}
                        viewport={{once: false, amount: 0.3}}
                        className='flex-1 border rounded-2xl flex flex-col gap-y-6 pb-24 p-6 items-start'>
                        <input
                            className='bg-transparent border-b py-3 outline-none w-full placeholder:text-white focus:border-accent transition-all'
                            type='text'
                            name='from_name'
                            id="from_name"
                            placeholder='Your name'
                        />
                        <input
                            className='bg-transparent border-b py-3 outline-none w-full placeholder:text-white focus:border-accent transition-all'
                            type='email'
                            name="reply_to"
                            id="reply_to"
                            placeholder='Your e-mail'
                        />
                        <input className='hidden' type="text" name="to_name" id="to_name" value="Arts ICT"/>
                        <textarea
                            className='bg-transparent border-b py-3 outline-none w-full placeholder:text-white focus:border-accent transition-all resize-none mb-12'
                            name="message"
                            id="message"
                            placeholder='Your message'
                        />
                        <button type='submit' id="button" className='btn btn-lg'>Send message</button>

                        <script type="text/javascript"
                                src="https://cdn.jsdelivr.net/npm/@emailjs/browser@4/dist/email.min.js"></script>

                        <script type="text/javascript">
                            emailjs.init('h7xiNrW-pTzv7e_3G')
                        </script>
                    </motion.form>
                </div>
            </div>
        </section>

    );
};
export default Contact;
